<template>
    <img :status="status" :src="url" class="component-lazyload" :lazy="statusString">
</template>
<script>
// import throttled from 'lodash/throttled'
export default {
    name: 'LazyLoad',

    props: {
        event: {
            type: String,
            default: 'scroll'
        },

        threshold: {
            type: Number,
            default: 500
        },

        src: {
            type: String,
        },

        placeholder: {
            type: String,
            default: 'data:image/jpeg;base64,'
        },

        attempt: {
            type: Number,
            default: 1
        },

        preLoad: {
            type: Number,
            default: 1.3
        },

        watch: {}
    },

    data() {
        return {
            status: -2,
            rect: {},
            url: '',
            loadTime: 0,
            timer: null
        };
    },

    mounted() {
        // syslog(this.$el.offsetParent) //  === null
        // // 找最近的overflowY == scroll 或者overflowX == scroll的父节点
        // var _parentNode = this.$el.parentNode;
        // var style = getComputedStyle(_parentNode, null);
        // // 不能等于document, document.nodeType == null 
        // while (9 != _parentNode.nodeType && 'scroll' != style.overflowY && 'scroll' != style.overflowX) {
        //     _parentNode = _parentNode.parentNode;
        //     style = getComputedStyle(_parentNode, null);
        // }
        // this.viewNode = _parentNode;

        this.url = this.placeholder;
        this.isInView() && this.loadImg();
    },

    methods: {
        isInView() {
            this.rect = this.$el.getBoundingClientRect();
            return this.rect.top < window.innerHeight * this.preLoad;
        },

        loadImg() {
            this.status = 0;
            // if(img.compulet);
            let img = new Image();
            img.src = this.src;

            // 加载成功
            img.onload = e => {
                this.status = 1;
                this.url = this.src;
                this.loadTime = e.timeStamp;
                // img = null;
            }

            // 错误处理
            img.onerror = e => {
                this.status = -1;
                syslog(e);
            }

        }
    },

    watch: {
        watch() {
            -2 == this.status && this.isInView() && this.loadImg();
        }
    },

    computed: {
        statusString() {
            switch (this.status) {
                case -1:
                    return 'error';
                    break;
                case 0:
                    return 'loading';
                    break;
                case 1:
                    return 'done';
                    break;
            }
        }
    }
}
</script>
<style scoped lang="scss">
@import '../../scss/theme.scss';
.component-lazyload {
    display: block;
    overflow: hidden;
    >img {
        width: 100%;
        display: block;
    }
    >img[lazy="loading"] {}
}
</style>
